import onDOMLoad from './on-dom-load';

function getFirstStyleSheet() {
  const [firstStyleSheet] = document.styleSheets;
  if (firstStyleSheet) {
    return firstStyleSheet;
  } else {
    const styleElement = document.createElement('style');
    styleElement.setAttribute('type', 'text/css');
    document.head.appendChild(styleElement);
    return styleElement.sheet;
  }
}

function insertCSSRule(selector, rule) {
  for (const cssStylesheet of document.styleSheets) {
    for (let i = 0; i < cssStylesheet.cssRules.length; i++) {
      const cssRule = cssStylesheet.cssRules[i];
      if (cssRule.selectorText && cssRule.selectorText.includes(selector)) {
        cssStylesheet.insertRule(rule, i + 1);
        return cssStylesheet.cssRules[i + 1];
      }
    }
  }
  throw new Error(
    'A CSS rule used by a Stylesheet component was not found. Make sure you imported the source CSS correctly'
  );
}

/**
 * @typedef {Attr} BoundAttr Attr bound to a CSSOM rule
 * @property {CSSRule} cssRule will be used to apply the result of the attr declaration
 * @property {string} className cssRule's class name
 */

/**
 * @param {Attr[]} attrs
 * @returns {BoundAttr[]}
 */
const bindAttrsToCSSOM = attrs => {
  const firstStyleSheet = getFirstStyleSheet();
  const boundAttrs = attrs.map(attr => {
    const className = 'a' + Math.random().toString(32).slice(6);
    const cssRuleIndex = firstStyleSheet.cssRules.length;
    firstStyleSheet.insertRule(`${ attr.selector }.${ className } {}`, cssRuleIndex);
    const cssRule = firstStyleSheet.cssRules[cssRuleIndex];
    return { ...attr, className, cssRule };
  });
  onDOMLoad(() => {
    for (const attr of boundAttrs) {
      const cssRuleIndex = [...firstStyleSheet.cssRules].indexOf(attr.cssRule);
      firstStyleSheet.deleteRule(cssRuleIndex);
      attr.cssRule = insertCSSRule(attr.selector, attr.cssRule.cssText);
    }
  });
  return boundAttrs;
};

export default bindAttrsToCSSOM;

import matchAttribute from '../../core/match-attribute';

const getClassName = ({ className }) => className;

const generateClassName = ({ className, attributes = [], attrs = [] }) => props =>
  [
    className,
    ...attrs.map(getClassName),
    ...attributes
      .filter(attribute => matchAttribute(attribute, props[attribute.name]))
      .map(getClassName),
  ].join(' ');

export default generateClassName;

export default function onDOMLoad(callback) {
  if (document.readyState === 'complete') {
    callback();
  } else {
    const handleDOMLoad = () => {
      removeEventListener('load', handleDOMLoad);
      callback();
    };
    addEventListener('load', handleDOMLoad);
  }
}

import { createElement, Component } from 'react';
import { format } from '../../core/template';
import bindAttrsToCSSOM from '../../dom/dist/bind-attrs-to-cssom';
import generateClassName from '../../dom/dist/generate-class-name';
import Stylesheet from '../hot';
import { omitBy } from './utils.js';

/**
 * @param {string} displayName to be displayed in the React Dev Tools wrapped with Styled()
 * @param {string} className to be used for basic styles bound to the component's tag name
 * @param {Array<Attribute>} attributes selectors the component can be bound to
 * @param {Array<Attr>} attrs in properties that the component can be bound to
 * @param {string} [base] tag the component uses by default
 * @param {Object} invalidProps to avoid passing to the component's DOM element
 * @returns {Class<React.Component>}
 */
module.exports = function createCSSComponent({
  displayName,
  className,
  attributes,
  attrs,
  base = 'div',
  invalidProps,
}) {
  return class CSSComponent extends Component {
    /** Defined on the component so they can be redefined and be updated in instances later */
    static displayName = `Styled(${ displayName })`;
    static className = className;
    static attributes = attributes;
    static attrs = attrs;
    static base = base;
    static invalidProps = invalidProps;

    constructor(props) {
      super(props);
      this.init();
      /** Register the instance for hot updates */
      Stylesheet.register(this);
    }

    /**
     * Defined outside the constructor as it can be reused when the component's static properties change.
     * Currently used for HMR
     */
    init() {
      this.attrs = bindAttrsToCSSOM(this.constructor.attrs);
      this.generateClassName = generateClassName({
        className: this.constructor.className,
        attributes: this.constructor.attributes,
        attrs: this.attrs,
      });
      this.applyAttrs(this.props);
    }

    componentWillUpdate(nextProps) {
      this.applyAttrs(nextProps);
    }

    componentWillUnmount() {
      Stylesheet.unregister(this);
    }

    /** Updates the element's CSS properties using attr() */
    applyAttrs = props => {
      for (const attr of this.attrs) {
        attr.cssRule.style[attr.prop] = format(attr.template, props);
      }
    };

    /**
     * @param {*} value of prop
     * @param {*} key of prop
     * @return {boolean}
     */
    shouldOmitProp = (value, key) => {
      return this.constructor.invalidProps[key] || key === 'innerRef';
    };

    render() {
      const { props } = this;
      return createElement(this.constructor.base, {
        ref: props.innerRef,
        ...omitBy(props, this.shouldOmitProp),
        className: this.generateClassName(this.props),
      });
    }
  };
};

export const omitBy = (object, filter) => {
  const newObj = {};
  for (const key in object) {
    const value = object[key];
    if (!filter(value, key)) {
      newObj[key] = value;
    }
  }
  return newObj;
};

import { format } from '../../core/template';
import matchAttribute from '../../core/match-attribute';
import bindAttrsToCSSOM from '../../dom/dist/bind-attrs-to-cssom';
import generateClassName from '../../dom/dist/generate-class-name';

const getAttributeClassNames = attributes => props =>
  attributes
    .filter(attribute => matchAttribute(attribute, props[attribute.name]))
    .map(attribute => attribute.className);

class CSSComponent {
  willUpdate = false;

  constructor(element, attrs, props = {}) {
    this.element = element;
    this.attrs = attrs;
    this.props = props;
  }

  observe(properties) {
    Object.defineProperties(
      this.element,
      properties.reduce(
        (acc, property) => ({
          ...acc,
          [property]: {
            get: () => {
              return this.props[property];
            },
            set: value => {
              this.update({ ...this.props, [property]: value });
              return value;
            },
          },
        }),
        {}
      )
    );
  }

  update(nextProps) {
    const prevProps = this.props;
    this.props = nextProps;
    if (!this.willUpdate) {
      this.willUpdate = true;
      setTimeout(() => {
        this.element.dispatchEvent(
          Object.assign(new Event('willUpdateStyle'), {
            props: prevProps,
            nextProps: this.props,
          })
        );
        this.willUpdate = false;
        requestAnimationFrame(() => {
          this.render();
          this.element.dispatchEvent(
            Object.assign(new Event('didUpdateStyle'), { prevProps, props: this.props })
          );
        });
      });
    }
  }

  render() {
    const { props } = this;
    this.element.className = this.className;
    for (const attr of this.attrs) {
      if (attr.cssRule) {
        attr.cssRule.style[attr.prop] = format(attr.template, props);
      }
    }
  }
}

const createComponent = ({ className, attributes, attrs, base = 'div' }) => class
  extends CSSComponent {
    static getAttributeClassNames = getAttributeClassNames(attributes);

    static create(initialAttributes) {
      const instance = new this(initialAttributes);
      return instance.element;
    }

    static propKeys = [
      ...attributes.map(attribute => attribute.name),
      ...attrs.reduce((acc, attr) => acc.concat(attr.attributes), []),
    ];

    static className = className;
    static attributes = attributes;
    static attrs = attrs;
    static base = base;

    constructor(props) {
      super(document.createElement(base), bindAttrsToCSSOM(attrs), props);
      this.observe(this.constructor.propKeys);
      this.render();
    }

    generateClassName = generateClassName({ className, attributes, attrs: this.attrs });

    get className() {
      return this.generateClassName(this.props);
    }
};

module.exports = createComponent;
